$=jQuery.noConflict();$(document).ready(function(){var eleccion="";$('.boton-actualidad').click(function(){eleccion='actualidad';cargar_noticias_ajax();});$('.boton-deportes').click(function(){eleccion='deportes';cargar_noticias_ajax();});function cargar_noticias_ajax(){var datos={'eleccion':eleccion}
$.ajax({type:'post',url:admin_url.ajax_url,data:{action:'cargarCategoria',valor:datos},beforeSend:function(){$('#contenedor-noticias').html(' cargando noticias');},success:function(){$('#contenedor-noticias').html(datos);},error:function(){console.log(error);}});}});