$=jQuery.noConflict();$(document).ready(function(){var CustomPluginMediaUploader={construct:function(){$('.custom-plugin-media-button').each(function(index){CustomPluginMediaUploader.initButton($(this));});},initButton:function(_that){_that.click(function(e){var metaImageFrame;var btn=e.target;if(!btn||!$(btn).attr('data-custom-plugin-media-uploader-target'))return;var field=$(btn).data('custom-plugin-media-uploader-target');e.preventDefault();metaImageFrame=wp.media.frames.metaImageFrame=wp.media({title:meta_image.title,button:{text:'Use this file'},});metaImageFrame.on('select',function(){var media_attachment=metaImageFrame.state().get('selection').first().toJSON();$(field).val(media_attachment.url);});metaImageFrame.open();});}};CustomPluginMediaUploader.construct();});